# This code sample uses the 'requests' library:
# http://docs.python-requests.org
import requests
import json

url = %bitbucket.restapi.url.repositories.workspace.slug.pullrequests%

headers = {
  "Accept": "application/json",
  "Content-Type": "application/json",
  "Authorization": "Bearer %bitbucket.restapi.url.access-token%"
}

payload = json.dumps({
  "title": "merge pull request",
  "description": "",
  "state": "OPEN",
    "author": {
    "display_name": "%env.COMMIT.author%",
    "type": "user",
    "nickname": "%env.COMMIT.author%",
    "account_id": ""
  },
  "source": {
    "repository": {
      "type": "repository"
    },
    "branch": {
      "name": "%env.git.branch.source%",
      "merge_strategies": [
        "merge_commit"
      ]
    }
  },
  "destination": {
    "repository": {
      "type": "repository"
    },
    "branch": {
      "name": "%env.git.branch.target%",
      "merge_strategies": [
        "merge_commit"
      ]
    }
  }
})
print(str(payload))
response = requests.request(
   "POST",
   url,
   data=payload,
   headers=headers
)
#'There are no changes to be pulled'
print(response.text)
err_json=json.loads(json.loads(response.text)['error'])
print(response.status_code)
if 201 != response.status_code:
  print(json.dumps(json_response, sort_keys=True, indent=4, separators=(",", ": ")))
  exit(1)
