parameters.param:
# This code sample uses the 'requests' library:
# http://docs.python-requests.org
import requests
import json

url = %bitbucket.restapi.url.repositories.workspace.slug.pullrequests%

headers = {
  "Accept": "application/json",
  "Content-Type": "application/json",
  "Authorization": "Bearer %bitbucket.restapi.url.access-token%"
}

payload = json.dumps( {
    "title": "merge pull request",
    "description": "",
    "state": "OPEN",
    "open": true,
    "closed": false,
    "fromRef": {
        "id": "%git.branch.source%",
        "repository": {
            "slug": "%bitbucket.restapi.respositories.slug%",
            "name": null,
            "project": {
                "key": "%COMMIT.author%"
            }
        }
    },
    "toRef": {
        "id": "%git.branch.target%",
        "repository": {
            "slug": "%bitbucket.restapi.respositories.slug%",
            "name": null,
            "project": {
                "key": "%COMMIT.author%"
            }
        }
    },
    "locked": false

})

response = requests.request(
   "POST",
   url,
   data=payload,
   headers=headers
)

print(json.dumps(json.loads(response.text), sort_keys=True, indent=4, separators=(",", ": ")))
