parameters.param:
# This code sample uses the 'requests' library:
# http://docs.python-requests.org
import requests
import json

url = %bitbucket.restapi.url.repositories.workspace.slug.pullrequests%

headers = {
  "Accept": "application/json",
  "Content-Type": "application/json",
  "Authorization": "Bearer %bitbucket.restapi.url.access-token%"
}

payload = json.dumps( {
    "title": "merge pull request",
    "description": "",
    "state": "OPEN",
    "source": {
    "repository": {
      "type": "%bitbucket.restapi.url.repositories.workspace.slug%"
    },
    "branch": {
      "name": "%env.git.branch.source%",
      "merge_strategies": [
        "merge_commit"
      ],

    },
    "commit": {
      "hash": "%env.COMMIT.hash%"
    }
  },
    "fromRef": {
        "id": "%env.git.branch.source%",
        "repository": {
            "slug": "%bitbucket.restapi.respositories.slug%",
            "name": "%bitbucket.restapi.respositories.slug%",
            "project": {
                "key": "%env.COMMIT.author%"
            }
        }
    },
    "toRef": {
        "id": "%env.git.branch.target%",
        "repository": {
            "slug": "%bitbucket.restapi.respositories.slug%",
            "name": "%bitbucket.restapi.respositories.slug%",
            "project": {
                "key": "%env.COMMIT.author%"
            }
        }
    }    
})

response = requests.request(
   "POST",
   url,
   data=payload,
   headers=headers
)
json_response=json.loads(response.text)
print(json.dumps(json_response, sort_keys=True, indent=4, separators=(",", ": ")))
if 'error' == json.type:
  exit 1
